import React, { useState, useEffect } from 'react';
import SearchBar from './components/SearchBar';
import RecipeCard from './components/RecipeCard';
import RecipeDetail from './components/RecipeDetail';
import './styles/App.css'; // Import CSS utama
import './styles/global.css'; // Import CSS global

function App() {
  const [searchTerm, setSearchTerm] = useState('');
  const [recipes, setRecipes] = useState([]);
  const [selectedRecipe, setSelectedRecipe] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [noResults, setNoResults] = useState(false);
  const [favorites, setFavorites] = useState(() => {
    // Muat favorit dari Local Storage saat inisialisasi
    const savedFavorites = localStorage.getItem('recipeFavorites');
    return savedFavorites ? JSON.parse(savedFavorites) : [];
  });
  const [showFavorites, setShowFavorites] = useState(false); // State untuk menampilkan favorit

  // Simpan favorit ke Local Storage setiap kali berubah
  useEffect(() => {
    localStorage.setItem('recipeFavorites', JSON.stringify(favorites));
  }, [favorites]);

  const fetchRecipes = async (query) => {
    if (!query) {
      setRecipes([]);
      setSelectedRecipe(null);
      setNoResults(false);
      setShowFavorites(false); // Sembunyikan favorit saat ada query kosong
      return;
    }

    setLoading(true);
    setError(null);
    setNoResults(false);
    setSelectedRecipe(null); // Pastikan detail tidak tampil setelah pencarian baru
    setShowFavorites(false); // Sembunyikan favorit saat melakukan pencarian

    try {
      const response = await fetch(`https://www.themealdb.com/api/json/v1/1/search.php?s=${query}`);
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      const data = await response.json();
      if (data.meals) {
        setRecipes(data.meals);
        setNoResults(false);
      } else {
        setRecipes([]);
        setNoResults(true);
      }
    } catch (e) {
      console.error("Error fetching recipes:", e);
      setError("Gagal mengambil resep. Silakan coba lagi nanti.");
      setRecipes([]);
      setNoResults(false);
    } finally {
      setLoading(false);
    }
  };

  const fetchRandomRecipe = async () => {
    setLoading(true);
    setError(null);
    setNoResults(false);
    setSelectedRecipe(null); // Clear previous selection
    setRecipes([]); // Clear previous search results
    setSearchTerm(''); // Clear search input
    setShowFavorites(false); // Sembunyikan favorit

    try {
      const response = await fetch(`https://www.themealdb.com/api/json/v1/1/random.php`);
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      const data = await response.json();
      if (data.meals && data.meals.length > 0) {
        setRecipes(data.meals); // Tampilkan resep acak di grid
        // Atau langsung tampilkan detail: setSelectedRecipe(data.meals[0]);
      } else {
        setNoResults(true);
      }
    } catch (e) {
      console.error("Error fetching random recipe:", e);
      setError("Gagal mengambil resep acak. Silakan coba lagi nanti.");
    } finally {
      setLoading(false);
    }
  };


  const fetchRecipeDetails = async (idMeal) => {
    setLoading(true);
    setError(null);
    try {
      const response = await fetch(`https://www.themealdb.com/api/json/v1/1/lookup.php?i=${idMeal}`);
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      const data = await response.json();
      if (data.meals && data.meals.length > 0) {
        setSelectedRecipe(data.meals[0]);
      } else {
        setSelectedRecipe(null);
        setError("Detail resep tidak ditemukan.");
      }
    } catch (e) {
      console.error("Error fetching recipe details:", e);
      setError("Gagal mengambil detail resep. Silakan coba lagi nanti.");
      setSelectedRecipe(null);
    } finally {
      setLoading(false);
    }
  };

  const handleSearchSubmit = (query) => {
    setSearchTerm(query);
    fetchRecipes(query);
  };

  const handleRecipeClick = (recipe) => {
    fetchRecipeDetails(recipe.idMeal);
  };

  const handleToggleFavorite = (recipe) => {
    setFavorites((prevFavorites) => {
      const isCurrentlyFavorite = prevFavorites.some((fav) => fav.idMeal === recipe.idMeal);
      if (isCurrentlyFavorite) {
        return prevFavorites.filter((fav) => fav.idMeal !== recipe.idMeal);
      } else {
        return [...prevFavorites, recipe];
      }
    });
  };

  const isRecipeFavorite = (recipe) => {
    return favorites.some((fav) => fav.idMeal === recipe.idMeal);
  };

  const handleShowFavorites = () => {
    setShowFavorites(true);
    setSelectedRecipe(null); // Pastikan detail tidak tampil
    setRecipes([]); // Kosongkan hasil pencarian
    setSearchTerm(''); // Kosongkan input pencarian
  };

  const displayedRecipes = showFavorites ? favorites : recipes;
  const isDisplayingFavorites = showFavorites && favorites.length > 0;
  const isDisplayingSearchResults = !showFavorites && recipes.length > 0 && !selectedRecipe;

  return (
    <div className="App">
      <header className="App-header">
        <h1>Temukan Resep Lezat</h1>
        <p>Jelajahi ribuan resep dari seluruh dunia atau temukan inspirasi baru!</p>
      </header>

      <SearchBar onSearch={handleSearchSubmit} onRandomSearch={fetchRandomRecipe} />

      <div className="action-buttons">
        <button onClick={handleShowFavorites}>
          Lihat Favorit ({favorites.length})
        </button>
      </div>

      {loading && <p className="loading">Memuat resep, mohon tunggu...</p>}
      {error && <p className="error-message">{error}</p>}
      {noResults && <p className="no-results">Tidak ada resep yang ditemukan untuk pencarian Anda.</p>}

      {selectedRecipe ? (
        <RecipeDetail recipe={selectedRecipe} onBack={() => setSelectedRecipe(null)} />
      ) : (
        <div className="results-grid">
          {isDisplayingFavorites && favorites.length === 0 && (
            <p className="no-results">Anda belum memiliki resep favorit.</p>
          )}
          {displayedRecipes.map((recipe) => (
            <RecipeCard
              key={recipe.idMeal}
              recipe={recipe}
              onClick={handleRecipeClick}
              onToggleFavorite={handleToggleFavorite}
              isFavorite={isRecipeFavorite(recipe)}
            />
          ))}
        </div>
      )}
    </div>
  );
}

export default App;
