import React from 'react';
import '../styles/components.css'; // Import CSS komponen

function RecipeCard({ recipe, onClick, onToggleFavorite, isFavorite }) {
  const handleFavoriteClick = (e) => {
    e.stopPropagation(); // Mencegah klik kartu saat klik favorit
    onToggleFavorite(recipe);
  };

  return (
    <div className="recipe-card" onClick={() => onClick(recipe)}>
      <div className="recipe-card-image-container">
        <img src={recipe.strMealThumb} alt={recipe.strMeal} />
        <span
          className={`favorite-icon ${isFavorite ? 'active' : ''}`}
          onClick={handleFavoriteClick}
          title={isFavorite ? "Hapus dari Favorit" : "Tambah ke Favorit"}
        >
          {isFavorite ? '‚ù§Ô∏è' : 'ü§ç'} {/* Ikon hati */}
        </span>
      </div>
      <div className="recipe-card-info">
        <h3>{recipe.strMeal}</h3>
        {/* Anda bisa menambahkan kategori atau area jika data tersedia */}
        {/* <p>{recipe.strCategory} | {recipe.strArea}</p> */}
        <button onClick={() => onClick(recipe)}>Lihat Detail</button>
      </div>
    </div>
  );
}

export default RecipeCard;
