import React from 'react';
import '../styles/components.css'; // Import CSS komponen

function RecipeDetail({ recipe, onBack }) {
  if (!recipe) return null;

  // Helper untuk mendapatkan semua bahan dan takaran
  const getIngredients = (meal) => {
    const ingredients = [];
    for (let i = 1; i <= 20; i++) {
      const ingredient = meal[`strIngredient${i}`];
      const measure = meal[`strMeasure${i}`];
      if (ingredient && ingredient.trim() !== '') {
        ingredients.push(`${measure ? measure.trim() + ' ' : ''}${ingredient.trim()}`);
      }
    }
    return ingredients;
  };

  const ingredientsList = getIngredients(recipe);

  return (
    <div className="recipe-detail">
      <div className="detail-header">
        <button onClick={onBack}>‚Üê Kembali</button>
        {/* Bisa tambahkan tombol favorit di sini juga */}
      </div>
      <h2>{recipe.strMeal}</h2>
      <img src={recipe.strMealThumb} alt={recipe.strMeal} />

      <div className="recipe-detail-content">
        <div className="ingredients-section">
          <h3>Bahan-bahan:</h3>
          <ul>
            {ingredientsList.map((item, index) => (
              <li key={index}>{item}</li>
            ))}
          </ul>
        </div>
        <div className="instructions-section">
          <h3>Instruksi:</h3>
          <p>{recipe.strInstructions}</p>
        </div>
      </div>
      {recipe.strYoutube && (
        <div style={{textAlign: 'center', marginTop: '30px'}}>
            <h3>Video Instruksi:</h3>
            <iframe
                width="100%"
                height="400"
                src={`https://www.youtube.com/embed/${recipe.strYoutube.split('v=')[1]}`}
                title="YouTube video player"
                frameBorder="0"
                allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
                allowFullScreen
                style={{borderRadius: '10px', boxShadow: '0 5px 15px rgba(0,0,0,0.1)'}}
            ></iframe>
        </div>
      )}
    </div>
  );
}

export default RecipeDetail;
